function createPinArray(length, value) {
  if (length < 1) {
    return [];
  }
  var values = new Array(length).fill("");
  if (value) {
    var splitted = value.trim().split("");
    for (var i = 0; i < Math.min(length, splitted.length); i += 1) {
      values[i] = splitted[i];
    }
  }
  return values;
}
export { createPinArray };
