import { validateValues } from './validate-values.js';
function validateFieldValue(path, rules, values) {
  if (typeof path !== "string") {
    return {
      hasError: false,
      error: null
    };
  }
  var results = validateValues(rules, values);
  var pathInError = Object.keys(results.errors).find(function (errorKey) {
    return path.split(".").every(function (pathPart, i) {
      return pathPart === errorKey.split(".")[i];
    });
  });
  return {
    hasError: !!pathInError,
    error: pathInError ? results.errors[pathInError] : null
  };
}
export { validateFieldValue };
