import { useRef } from 'react';
import { useIsomorphicEffect } from '../use-isomorphic-effect/use-isomorphic-effect.js';
var MIME_TYPES = {
  ico: "image/x-icon",
  png: "image/png",
  svg: "image/svg+xml",
  gif: "image/gif"
};
function useFavicon(url) {
  var link = useRef();
  useIsomorphicEffect(function () {
    if (!url) {
      return;
    }
    if (!link.current) {
      var existingElements = document.querySelectorAll('link[rel*="icon"]');
      existingElements.forEach(function (element2) {
        return document.head.removeChild(element2);
      });
      var element = document.createElement("link");
      element.rel = "shortcut icon";
      link.current = element;
      document.querySelector("head").appendChild(element);
    }
    var splittedUrl = url.split(".");
    link.current.setAttribute("type", MIME_TYPES[splittedUrl[splittedUrl.length - 1].toLowerCase()]);
    link.current.setAttribute("href", url);
  }, [url]);
}
export { useFavicon };
