function createConverter(units) {
  return function (px) {
    if (typeof px === "number") {
      return "".concat(px / 16).concat(units);
    }
    if (typeof px === "string") {
      var replaced = px.replace("px", "");
      if (!Number.isNaN(Number(replaced))) {
        return "".concat(Number(replaced) / 16).concat(units);
      }
    }
    return px;
  };
}
var rem = createConverter("rem");
var em = createConverter("em");
export { em, rem };
