'use client';

function zodResolver(schema) {
  return function (values) {
    var parsed = schema.safeParse(values);
    if (parsed.success) {
      return {};
    }
    var results = {};
    parsed.error.errors.forEach(function (error) {
      results[error.path.join(".")] = error.message;
    });
    return results;
  };
}
export { zodResolver };
